import React, { useState } from 'react';
import './custom.css';
import Modal from 'react-bootstrap/Modal';

function Suggestions(props) {

        const [show, setShow] = useState(false);

        const handleClose = () => setShow(false);
        const handleShow = () => setShow(true);
    
    if (props.results.length > 0) {
    const options = props.results.slice(0, 5).map((x, y) => (
        <div id="clickable-div" onClick={handleShow}><li key={y}>{x.title}</li></div>
    ))
    
    return <div>
        <Modal show={show} onHide = {handleClose}>
            <Modal.Header closeButton>
                <Modal.Title>Success!</Modal.Title>
            </Modal.Header>
            <Modal.Body>Your film has been logged, and your data should now be updated.</Modal.Body>
        </Modal>
        <div>{options}</div>
     
        </div>;


    } else if (props.typed) { 
        return <div><p>No results</p></div> 
    } else return null;
        
}

export default Suggestions;
