import React, { useState } from 'react';
import axios from 'axios';


function RecsList(props) {

    const [rec, setRec] = useState('');

        axios.get('https://api.themoviedb.org/3/list/138980?api_key=d1e40fddb73df31e61693cbf7ef094aa')
        .then(x => {

            let index = Math.floor(Math.random() * x.data.item_count);
             setRec(x.data.items[index]["original_title"]);
          console.log(x.data.item_count)   
        });
        
     return <p>{rec}</p>
    
}

export default RecsList;
