import axios from 'axios';


function RecsList(props) {

    let rec= '';

        const setRec= () => {
            axios.get('https://api.themoviedb.org/3/list/138980?api_key=d1e40fddb73df31e61693cbf7ef094aa')
        .then(x => {
          rec = (x.data.items[5]["original_title"]);
          console.log(x.data)   
        });

    }

    setRec();
        
     return (rec !== '') ? <p>{rec}</p> : setRec();
    
}

