import React from 'react';
import axios from 'axios';


function RecsList(props) {

    let rec = '';

        axios.get('https://api.themoviedb.org/3/list/138980?api_key=d1e40fddb73df31e61693cbf7ef094aa')
        .then(x => {
             rec = x.data.items[0]["original_title"];
             console.log(props.filmIndex, rec);
        })
        
     return <div>{props.filmIndex, rec}</div> 
    
}




export default RecsList;
