import React from 'react';

class Suggestions extends React.Component {
    constructor(props) {
        super(props);
        this.handleClick = this.handleClick.bind(this);
    }

    state = {typed: true}

    handleClick() {
        //write clickable link to add film here
    }

    componentDidUpdate() {
        this.setState({typed: true})
    }

    render() {
    if (this.props.results.length > 0) {
    const options = this.props.results.slice(0, 5).map((x, y) => (
        <li key={y} onClick={this.handleClick}>{x.title}</li>
    ))
    return <ul>{options}</ul>

    } else if (this.state.typed) { 
        return <div><p>No results</p></div> 
    } else return null;
        
}
}

export default Suggestions
