import axios from 'axios';


function RecsList(props) {

    const [rec, setRec] = useState('');

    useEffect((
        axios.get('https://api.themoviedb.org/3/list/138980?api_key=d1e40fddb73df31e61693cbf7ef094aa')
        .then(x => {
            let filmNum = Math.floor(Math.random() * (x.data.item_count));
            console.log(x.data)
          setRec(x.data.items[filmNum]["title"] + "?");
    })
    ), []);
    
    return
        <p>{rec}</p>
    
}

export default RecsList;
