import React from 'react';
import axios from  'axios';

export default class Tmdb extends React.Component {
    url = 'https://api.themoviedb.org/3/account/fidgetjnr/watchlist/movies?api_key=d1e40fddb73df31e61693cbf7ef094aa&session_id=d38ef9ba5672c0c9660f9bdd47b123bf609c2a13'
    state = {
        language: []
    };

    componentDidMount() {
        axios.get(this.url)
        .then(x => {
            const language = x.data.results;
            console.log(typeOf(language));
            this.setState({language});
        });
    }

    render() {

        return (
            <ul>
               {(this.state.language[0]) ? this.state.language[0].original_language: "Loading..."} 
            </ul>
        ) 
    }
}
