import React, { useState } from 'react';
import './custom.css';
import Modal from 'react-bootstrap/Modal';

class Suggestions extends React.Component {
    constructor(props) {
        super(props);

        const [show, setShow] = useState(false);

        const handleClose = () => setShow(false);
        const handleShow = () => setShow(true);
    }

    render() {
    if (this.props.results.length > 0) {
    const options = this.props.results.slice(0, 5).map((x, y) => (
        <div id="clickable-div" onClick={this.handleShow}><li key={y}>{x.title}</li></div>
    ))
    
    return <div>
        <Modal show={this.show} onHide = {this.handleClose}>
            <Modal.Header closeButton>
                <Modal.Title>Saying hello</Modal.Title>
            </Modal.Header>
        </Modal>
        <div>{options}</div>
     
        </div>;


    } else if (this.props.typed) { 
        return <div><p>No results</p></div> 
    } else return null;
        
}
}

export default Suggestions;
