import React from 'react';
import axios from  'axios';
import "./custom.css"
import FilmInfo from './FilmInfo';
import Recommend from './Recommend';
import AddFilm from './AddFilm';

export default class Tmdb extends React.Component {

    i = 2;
    pages = 7;
    apiData =[];

    url = 'https://api.themoviedb.org/3/account/{account_id}/watchlist/movies?api_key=d1e40fddb73df31e61693cbf7ef094aa&session_id=d38ef9ba5672c0c9660f9bdd47b123bf609c2a13'
    state = {
        foreignFilms: [],
        englishFilms: [],
        percentage: 0,
        test: 0
    };

    yerMaw() {
        axios.get(this.url + "&page=" + this.i)
             .then(y => {
             this.apiData.push(...y.data.results);
             if (this.i <= this.pages) {this.yerMaw()};
             })
    }

    componentDidMount() {

        console.log("mounted");

        axios.get(this.url)
        .then(x => {
            let apiData = x.data.results;

            if (this.i <= this.pages) {  this.yerMaw() }

                axios.get(this.url + "&page=" + this.i)
                .then(y => {
                apiData.push(...y.data.results);

            let foreignFilms = apiData.filter (z => z.original_language !== "en");
            let englishFilms = apiData.filter (eng => eng.original_language == "en");
            let percentage = (foreignFilms.length/apiData.length) * 100;
            if (!Number.isInteger(percentage)) { percentage = percentage.toFixed(2) };
            this.setState({foreignFilms, englishFilms,  percentage});
            this.i++;

                })
            })

        }

    render() { 

        return (
        <div>
            <FilmInfo foreign={this.state.foreignFilms} english= {this.state.englishFilms} percentage={this.state.percentage} />
            <br />
            <div className="buttons"><Recommend /><AddFilm /></div>

        </div>
        ) 
    }
}
