import "./custom.css";
import React from 'react';
import Button from 'react-bootstrap/Button';

class FilmInfo extends React.Component {
    constructor(props) {
        super(props);
        this.handleClick = this.handleClick.bind(this);
        this.state = {
        clicked: false
    }
}

handleClick() {
    let clicked = true;
    this.setState({clicked});
}
    render() {
        
        let fill = 0;
        if (this.state.clicked) { fill = (this.props.percentage)};

        return (
            <div>
            
            <p>You have watched {this.props.foreign.length} foreign language films and {this.props.english.length} English language films.
            </p>
            <p>This means that {this.props.percentage}% of your viewing has been in a foreign language.</p>
          
            <p>{(this.props.percentage >= 50) ? "Great!" : "Do better."}</p>

          <Button onClick={this.handleClick}>yo</Button>

          <div id="prbar">
              {(this.state.clicked)}
              <div id="prpos">
              </div>
          </div>

            <div id="prbar">
              <div id="prpos" style={{width: this.props.percentage + "%"}}>
                  </div>
                  </div>
                </div>
        )
    }
}

export default FilmInfo;
