import React, { useState } from 'react';
import './custom.css';
import { Alert } from 'react-bootstrap';

class Suggestions extends React.Component {
    constructor(props) {
        super(props);
        this.handleClick = this.handleClick.bind(this);
    }



    handleClick() {
        const [show, setShow] = useState(true);

        if (show) {
            return (
                <Alert variant="success" onClose={() => setShow(false)} dismissable>
                    <Alert.Heading>Success!</Alert.Heading>
                    <p>Your film has been successfully logged, and your stats should now update.</p>
                </Alert>
            );
        }
    }

    render() {
    if (this.props.results.length > 0) {
    const options = this.props.results.slice(0, 5).map((x, y) => (
        <div id="clickable-div" onClick={this.handleClick}><li key={y}>{x.title}</li></div>
    ))
    return <ul>{options}</ul>

    } else if (this.props.typed) { 
        return <div><p>No results</p></div> 
    } else return null;
        
}
}

export default Suggestions
