import React from 'react';
import Button from 'react-bootstrap/Button';
import Popover from 'react-bootstrap/Popover';
import OverlayTrigger  from 'react-bootstrap/OverlayTrigger';
import FilmSearch from './FilmSearch';

class AddFilm extends React.Component {
    constructor(props) {
        super(props);
        this.popHide = this.popHide.bind(this);
    }

    popHide() {
        this.refs.calOver.hide();
    }

        render() {
        return (
        <div>
            <OverlayTrigger
            ref='calOver'
            show={false}
            rootClose
            trigger="click"
            data-trigger="focus"
            key="bottom"
            placement="bottom"
            overlay={
                <Popover id="popover-positioned-bottom">
                <Popover.Title as="h3">Search film</Popover.Title>
                <Popover.Content><FilmSearch popper={this.popHide} /></Popover.Content>
                </Popover>
            }
            >
                <Button variant="primary" onClick="">I watched something!</Button>
            </OverlayTrigger>
            </div>
        )}
}
export default AddFilm;
