import React from 'react';
import axios from  'axios';
import ProgressBar from 'react-bootstrap/ProgressBar';
import Button from 'react-bootstrap/Button';
import "./custom.css"
import FilmInfo from './FilmInfo';

export default class Tmdb extends React.Component {
    constructor(props) {
        super(props);
    this.handleClick = this.handleClick.bind(this);
    }

    url = 'https://api.themoviedb.org/3/account/fidgetjnr/watchlist/movies?api_key=d1e40fddb73df31e61693cbf7ef094aa&session_id=d38ef9ba5672c0c9660f9bdd47b123bf609c2a13'
    state = {
        foreignFilms: [],
        englishFilms: [],
        percentage: 0,
        index: 0
    };

    handleClick() {
        const index = Math.floor(Math.random() * 10);
        this.setState({index});
    }

    componentDidMount() {
        axios.get(this.url)
        .then(x => {
            const apiData = x.data.results;
            const foreignFilms = apiData.filter (y => y.original_language !== "en");
            const englishFilms = apiData.filter (z => z.original_language == "en");
            const percentage = (foreignFilms.length/apiData.length) * 100;
            if (!Number.isInteger(percentage)) { percentage = percentage.toFixed(2) };
            this.setState({foreignFilms, englishFilms, percentage});
        });
    }

    render() {
        if (!this.state.foreignFilms.length) {
            return <div />
        }

        return (
        <div>
            <p>You have watched {this.state.foreignFilms.length} foreign language films and {this.state.englishFilms.length} English language films.
            </p>
            <p>This means that {this.state.percentage}% of your viewing has been in a foreign language.</p>
          
            <p>{(this.state.percentage >= 50) ? "Great!" : "Do better."}</p>

            <div className="custom-progress-bg"><span className="custom-progress" style={{width: "55%"}} /></div>
            <p><Button variant="primary" onClick={this.handleClick}>Recommend me a film!</Button></p>
            <p>{this.state.index}</p>
            <FilmInfo foreign={this.state.foreignFilms} english= {this.state.englishFilms} percentage={this.state.percentage} />

        </div>
        ) 
    }
}
