import React from 'react';
import axios from  'axios';
import "./custom.css"
import FilmInfo from './FilmInfo';
import Recommend from './Recommend';
import AddFilm from './AddFilm';

export default class Tmdb extends React.Component {

    constructor(props) {
        super(props);
        this.refresher = this.refresher.bind(this);
    }

    i = 2;
    apiData =[];
    testArr = [];

    url = 'https://api.themoviedb.org/3/account/{account_id}/watchlist/movies?api_key=d1e40fddb73df31e61693cbf7ef094aa&session_id=d38ef9ba5672c0c9660f9bdd47b123bf609c2a13';

    state = {
        foreignFilms: [],
        englishFilms: [],
        percentage: 0,
        complete: true
    };

    yerMaw() {
        console.log("start" + this.i);
        axios.get(this.url + "&page=" + this.i)
             .then(x => {
             this.apiData.push(...x.data.results);
             this.i++;
             if (this.i <= x.data.total_pages) {this.yerMaw()}
             else this.yerDa();
             })
    }

    componentDidMount() {

        axios.get(this.url)
        .then(x => {
            this.apiData.push(...x.data.results);
            let pages = x.data.total_pages;
            if (this.i <= pages) { this.yerMaw() }
        })
    }

    componentDidUpdate() {
        console.log("updated +" + this.state.complete);
    }

    refresher() {
        axios.get(this.url + "&page=" + (this.i-1))
        .then(x => {
            if (x.data.total_pages == (this.i)) {
                this.yerMaw();
                } else {
                    x.data.results.map(y => {
                        if (this.apiData.indexOf(y) == -1){this.apiData.push(y); console.log(this.apiData)}       
                })   
            }
        })

        //if they are the same, get current page data (then run indexOf, comparison)
        //if they are different, run "this.yerMaw()";
    }

    yerDa() {

            let foreignFilms = this.apiData.filter (z => z.original_language !== "en");
            let englishFilms = this.apiData.filter (eng => eng.original_language == "en");
            let percentage = (foreignFilms.length/this.apiData.length) * 100;
            if (!Number.isInteger(percentage)) { percentage = percentage.toFixed(2) };
            this.setState({foreignFilms, englishFilms, percentage, complete: !this.state.complete});
    }

    render() { 

        

        return (
        <div>
            <FilmInfo foreign={this.state.foreignFilms} english={this.state.englishFilms} percentage={this.state.percentage} />
            <br />
            <div className="buttons"><Recommend /><AddFilm updater={this.refresher} /></div>

        </div>
        ) 
    }
}
