import React from 'react';
import Button from 'react-bootstrap/Button';

class Recommend extends React.Component {
 constructor(props) {
     super(props);
     this.handleClick = this.handleClick.bind(this);
     this.state = {
         index: 0,
         clicked: false
     }
 }   

 handleClick() {
    let index = Math.floor(Math.random() * 10);
    let clicked = true;
    this.setState({index, clicked});
}

render() {
    return (
<div>
    {(this.state.clicked)} ? <p>{this.state.index}</p> : <p><Button variant="primary" onClick={this.handleClick}>Recommend me a film!</Button></p>


</div>

    )
}

}

export default Recommend;
