import React, { useState } from 'react';
import './custom.css';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';

function Suggestions(props) {

        const [show, setShow] = useState(false);

        const handleClose = () => setShow(false);
        const filmClick = (film) => (setShow(true), sendFilm(film));

        const sendFilm = (film) => {
            console.clear();
            console.log(film);
        }
        
    if (props.results.length > 0) {
    const options = props.results.slice(0, 5).map((x, y) => (
        <div id="clickable-div" onClick={filmClick(y)}><li key={y}>{x.title}</li></div>
    ))
    
    return <div>
        <Modal show={show} onHide = {handleClose} animation={true}>
            <Modal.Header closeButton>
                <Modal.Title>Success!</Modal.Title>
            </Modal.Header>
            <Modal.Body>Your film has been logged, and your data should now be updated.</Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>Okedoke</Button>
            </Modal.Footer>
        </Modal>
        <div>{options}</div>
     
        </div>;


    } else if (props.typed) { 
        return <div><p>No results</p></div> 
    } else return null;
        
}

export default Suggestions;
