import React, { useState, useEffect } from 'react';
import axios from 'axios';


function RecsList(props) {

    const [blah, setBlah] = useState('');


        useEffect(() => {
        axios.get('https://api.themoviedb.org/3/list/138980?api_key=d1e40fddb73df31e61693cbf7ef094aa')
        .then(x => {
            let filmNum = Math.random() * x.data.item_count;
          setBlah(x.data.items[filmNum]["original_title"]);
          console.log(filmNum)   
        });
    });
        
     return <p>{blah}</p> 
    
}

export default RecsList;
