import React, { useState } from 'react';
import './custom.css';
import SuccessMsg from './SuccessMsg';


class Suggestions extends React.Component {
    constructor(props) {
        super(props);
        this.handleClick = this.handleClick.bind(this);
    }

    state = { showPopup: false };

    handleClick() {
        this.setState({showPopup: !this.state.showPopup});
    }


    render() {
    if (this.props.results.length > 0) {
    const options = this.props.results.slice(0, 5).map((x, y) => (
        <div id="clickable-div" onClick={this.handleClick}><li key={y}>{x.title}</li></div>
    ))
    
    return (
    <div>{options}
    {this.state.showPopup ? <SuccessMsg closePopup = {this.handleClick}/> : null}
    </div>
    )

    } else if (this.props.typed) { 
        return <div><p>No results</p></div> 
    } else return null;
        
}
}

export default Suggestions
