import React from 'react';
import Button from 'react-bootstrap/Button';
import FilmInfo from './FilmInfo';

class Recommend extends React.Component {
 constructor(props) {
     super(props);
     this.filmList = ["Sexmission", "Audition", "Stalker"];
     this.handleClick = this.handleClick.bind(this);
     this.state = {
         clicked: false,
         rec: ''
     }
 }   

 handleClick() {
    let index = Math.floor(Math.random() * 3);
    let rec = this.filmList[index];
    let clicked = true;
    console.log(rec);
    this.setState({clicked, rec});
}

render() {
    return (
<div>
    {(this.state.clicked) ? <div><p>{this.state.rec}</p><FilmInfo clicked={clicked} /></div> : <p><Button variant="primary" onClick={this.handleClick}>Recommend me a film!</Button></p>}


</div>

    )
}

}

export default Recommend;
